import React, { useState } from 'react';
import DateRangePicker from 'react-bootstrap-daterangepicker';
import borderOutlet from '../../../assets/svg/border_outer.svg';
import datePickerIcon from '../../../assets/svg/date-picker.svg';
import placePickerIcon from '../../../assets/svg/place-picker.svg';
import UiButton from '../../../components/UiButton';
import PackageListItem from '../../../components/PackageListItem';
import { PackagingHeader } from './components';

const timeFilter = ['Day', 'Week', 'Month', 'Year'];
const test = [1, 2, 3, 4];

const Assets = () => {
  const [selectedPackages, setSelectedPackages] = useState({});

  const handlePackageSelect = (id) => {
    const clone = { ...selectedPackages };

    if (clone[id]) {
      delete clone[id];
    } else {
      clone[id] = true;
    }
    setSelectedPackages(clone);
  };

  return (
    <div className="packaging">
      <div className="dashboard-container">
        <PackagingHeader />
        <div className="space-25" />
        <h1 className="packaging-title">What to pack</h1>
        <div className="space-25" />
        <div className="assets-list">
          {test.map((el) => (
            <PackageListItem
              key={el}
              onclick={() => handlePackageSelect(el)}
              selected={selectedPackages[el]}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Assets;
