import React, { useState } from 'react';
import cx from 'classnames';
import { ReactSVG } from 'react-svg';

import diagramIcon from '../../../assets/svg/leaderboard.svg';
import accountIcon from '../../../assets/svg/people.svg';
import settingsIcon from '../../../assets/svg/settings.svg';
import { DashboardTab, AccountsTab, SettingsTab } from './components/Tabs';

const Organization = () => {
  const [activeMenuTab, setActiveMenuTab] = useState('Dashboard');
  const setActiveMenuHandler = (tabName) => {
    setActiveMenuTab(tabName);
  };

  const viewActiveTab = (activeMenuTab) => {
    switch (activeMenuTab) {
      case 'DASHBOARD_TAB':
        return <DashboardTab />;
      case 'ACCOUNTS_TAB':
        return <AccountsTab />;
      case 'SETTINGS_TAB':
        return <SettingsTab />;
      default:
        return <DashboardTab />;
    }
  };

  return (
    <div className="organization-container">
      <div className="sidebar">
        <div className="organization-sidebar">
          <div
            role="presentation"
            className="organization-sidebar__item"
            onClick={() => setActiveMenuHandler('DASHBOARD_TAB')}
          >
            <ReactSVG
              className={cx('svg', {
                'svg-active': activeMenuTab === 'DASHBOARD_TAB',
              })}
              src={diagramIcon}
            />{' '}
            Dashboard
          </div>
          <div
            role="presentation"
            className="organization-sidebar__item"
            onClick={() => setActiveMenuHandler('ACCOUNTS_TAB')}
          >
            <ReactSVG
              className={cx('svg', {
                'svg-active': activeMenuTab === 'ACCOUNTS_TAB',
              })}
              src={accountIcon}
            />{' '}
            Accounts
          </div>
          <div
            role="presentation"
            className="organization-sidebar__item"
            onClick={() => setActiveMenuHandler('SETTINGS_TAB')}
          >
            <ReactSVG
              className={cx('svg', {
                'svg-active': activeMenuTab === 'SETTINGS_TAB',
              })}
              src={settingsIcon}
            />{' '}
            Settings
          </div>
        </div>
      </div>
      <div className="content">
        <div className="organization-container__heading">Dashboard</div>
        {viewActiveTab}
      </div>
      <div className="sidebar" />
    </div>
  );
};
export default Organization;
